// Check if message contains @web trigger
const message = $input.item.json.chatInput || '';
const hasWebSearch = message.includes('@web');

// Extract the actual query by removing @web
const cleanQuery = message.replace(/@web/g, '').trim();

return {
  json: {
    originalMessage: message,
    cleanQuery: cleanQuery,
    needsWebSearch: hasWebSearch,
    chatInput: cleanQuery
  }
};

parameters.prompt:
=Based on this user query, generate exactly 5 different search queries that would help find relevant information. Return ONLY the search queries, one per line, nothing else.

User Query: {{ $json.cleanQuery }}

Search Queries:

parameters.jsCode:
// Parse the 5 search queries from Ollama response
const response = $input.item.json.output || $input.item.json.text || '';
const queries = response
  .split('\n')
  .map(q => q.trim())
  .filter(q => q.length > 0 && !q.startsWith('#'))
  .slice(0, 5);

// Return each query as a separate item for parallel processing
return queries.map(query => ({
  json: {
    searchQuery: query,
    originalQuery: $input.item.json.cleanQuery
  }
}));

// Extract relevant info from DuckDuckGo response
const data = $input.item.json;
const query = $input.item.json.searchQuery || 'unknown';

let searchResult = {
  query: query,
  abstract: data.Abstract || '',
  abstractSource: data.AbstractSource || '',
  abstractURL: data.AbstractURL || '',
  relatedTopics: (data.RelatedTopics || []).slice(0, 3).map(t => ({
    text: t.Text || '',
    url: t.FirstURL || ''
  })),
  results: (data.Results || []).slice(0, 3).map(r => ({
    text: r.Text || '',
    url: r.FirstURL || ''
  }))
};

// Format as text for LLM
let resultText = `Query: ${query}\n`;
if (searchResult.abstract) {
  resultText += `Summary: ${searchResult.abstract}\n`;
  resultText += `Source: ${searchResult.abstractSource} (${searchResult.abstractURL})\n`;
}
if (searchResult.relatedTopics.length > 0) {
  resultText += `Related:\n${searchResult.relatedTopics.map(t => `- ${t.text}`).join('\n')}\n`;
}

return {
  json: {
    ...searchResult,
    formattedResult: resultText,
    originalQuery: $input.item.json.originalQuery
  }
};

// Combine all search results into one context
const allResults = $input.item.json.data || [];
const originalQuery = allResults[0]?.originalQuery || $input.item.json.originalQuery;

const combinedResults = allResults
  .map(r => r.formattedResult || '')
  .filter(r => r.length > 0)
  .join('\n---\n');

return {
  json: {
    originalQuery: originalQuery,
    searchResults: combinedResults,
    resultCount: allResults.length,
    chatInput: originalQuery // For AI Agent
  }
};
